DROP TABLE IF EXISTS `lemonuser`;
CREATE TABLE `lemonuser` (
  `id` varchar(32) NOT NULL,
  `username` varchar(32) DEFAULT NULL,
  `pwd` varchar(32) DEFAULT NULL,
  `weixin` varchar(32) DEFAULT NULL COMMENT '微信号',
  `cell_phone` varchar(11) DEFAULT NULL COMMENT '手机号',
  `qq` varchar(32) DEFAULT NULL COMMENT 'qq',
  `email` varchar(32) DEFAULT NULL COMMENT '邮箱',
  `shenfen` varchar(32) DEFAULT NULL COMMENT '身份证号',
  `statusd` varchar(2) DEFAULT NULL COMMENT '用户状态，2、监禁',
  `true_name` varchar(32) DEFAULT NULL COMMENT '真实姓名',
  `regist_time` datetime DEFAULT NULL COMMENT '注册时间',
  `user_group` varchar(2) DEFAULT NULL COMMENT '用户组',
  `user_level` varchar(2) DEFAULT NULL COMMENT '用户级别',
  `photo` varchar(32) DEFAULT NULL COMMENT '头像',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

